INSERT INTO `books` (`id`, `name`, `year`, `author`, `description`, `country`, `banner`, `categories_books_form_id`, `categories_books_content_id`) VALUES
(1, 'ОНО', 1986, 'Стивен Кинг', 'Волна страшных убийств захватила небольшой американский город Дерри. Один за одним погибают дети, но полиция бессильна. Маньяк не оставляет следов, нет ни единой зацепки.\r\nСемеро одиннадцатилетних ребят решаются найти убийцу – жестокое нечто по имени Оно. Каждому из них предстоит пережить встречу с чудовищем, и никто из взрослых не придет на помощь. Ведь Оно – воплощение детских страхов, которое взрослые просто не способны увидеть.', 'США', '/images/books images/378_o.jpg', 2, 10),
(2, 'Знак четырех', 1890, 'Артур Конан Дойль', 'Мери Морстен приходит к Шерлоку Холмсу с двумя загадками. Первая касалась исчезновения ее отца 10 лет назад, через несколько лет после которого она стала получать по почте редкие жемчужины от неизвестного покровителя.\r\n“Знак четырех” имеет сложный сюжет, завязка ее скрыта в событиях, произошедших в Ост-Индской компании, где четверых каторжников и двух продажных надзирателей связала цепь из смерти, коварства, ненависти и мести. Кто бы мог подумать, что на таком фундаменте появится трогательная история любви доктора Ватсона. Кроме того, в повести более подробно раскрывается образ Шерлока Холмса, выдавая его человеческие слабости и приближая к читателю', 'Англия', '/images/books images/610.jpg', 2, 8),
(3, 'Собака Баскервилей', 1902, 'Артур Конан Дойль', 'Сэр Чарльз Баскервиль найден мертвым на окраине Баскервиль-холла, у калитки, ведущей на болота. Искаженное ужасом лицо и никаких признаков насилия. Лишь собачьи следы.\r\nСэр Генри Баскевиль, единственный из известных наследников, должен прибыть из Торонто, и семейный врач Баскервилей Джеймс Мортимер, опасаясь за жизнь наследника просит Шерлока Холмса определить причину смертельного сердечного приступа своего пациента. Причина заподозрить убийство у доктора веская. Старинное семейное проклятие, связанное с собакой.', 'Англия', '/images/books images/566_o.jpg', 3, 8),
(4, 'Странная история доктора Джекила и мистера Хайда', 1886, 'Роберт Льюис Стивенсон', 'Чего только не вынырнет из лондонского тумана. Вот, например, у дома респектабельного доктора Генри Джекила часто стал возникать тип омерзительной наружности, агрессивный и жестокий. Каково же было удивление уважаемого нотариуса Аттерсона, когда именно этому типу, Эдварду Хайду, достопочтенный доктор Джекил завещал все свое имущество.', 'Англия', '', 3, 10),
(5, 'Зов Ктулху', 1928, 'Говард Филлипс Лавкрафт', 'Первый том собрания сочинений Лавкрафта в данной серии. В него вошли преимущественно короткие рассказы, написанные Лавкрафтом в первые десять лет основного периода его творчества, с 1917 по 1926 год.', 'США', '/images/books images/516_o.jpg', 4, 10),
(6, 'Хребты Безумия', 1936, ' Говард Филлипс Лавкрафт', 'Геолог Уильям Дайер, профессор мискатоникского университета, рассказывает леденящую душу историю, в надежде удержать запланированную и получившую широкую огласку экспедицию в Антарктику. Предыдущая экспедиция, возглавляемая профессором, обнаружила фантастические руины и опасную тайну, выходящую за пределы человеческого знания.\r\nПодразделение его группы обнаружило останки четырнадцати древних форм жизни неизвестного происхождения и принадлежности. Шесть из них оказались сильно повреждены, остальные – в полной сохранности. Вскоре, основная экспедиция теряет связь с этим подразделением и отправляется на место его стоянки. То, что они там обнаруживают, не поддается никакому логическому объяснению.', 'США', '', 2, 10),
(7, 'Вий', 1835, 'Николай Васильевич Гоголь', 'Перевирая слова классика, \"Нет повести ужаснее на свете, чем повесть о Вие\". Звучит не складно, но суть, я думаю, ясна. \r\nНиколай Васильевич известен каждому школьнику как мастер запугать до полусмерти так, что потом всю ночь от страха глаз не сомкнуть. Но тут он, можно сказать, переплюнул сам себя.\r\nЯ с трудом припомню в мировой литературе пример настолько леденящего кровь произведения, как \"Вий\". Может только Эдгар По составил бы конкуренцию, но и то спорный вопрос.\r\n\"Вий\" - это образцовый, канонический ужас всех ужасов. Он будит то животное, глубинное, впитанное с молоком матери еще то далекое, отсылающее к дохристианскому времени, и свойственное каждому славянину особое чувство мистического ужаса. Думаете, нет в вас такого? А вы рискните, почитайте.', 'Российская империя', '', 3, 10),
(8, 'Властелин Колец. Братство Кольца', 1954, 'Джон Рональд Руэл Толкин', 'Племянник Бильбо Бэггинса, хоббит Фродо, даже и не подозревал, какое на самом деле наследство оставил ему его дядюшка. Однажды теплым весенним утром на пороге дома Фродо появляется волшебник Гэндальф. И привычный мир встает с ног на голову, страшная война из слухов становится частью жизни, а коварные и жуткие слуги Врага готовы на все, лишь бы вернуть потерянное своему господину. Сквозь препятствия и потери отряд Хранителей отправляется в путь.', 'Англия', '', 1, 9),
(9, 'Властелин Колец. Две Твердыни', 1954, 'Джон Рональд Руэл Толкин', 'Отряд Хранителей распадается, а война приходит на степи Рохана — государства Всадников, союзников Гондора. Арагорн, Гимли и Леголас, а также младшие хоббиты помогают рохирримам в решающей битве против мага Сарумана, а путь Фродо и Сэма лежит во тьму, к Пепельным горам, и дальше, в страшную крепость Кирит-Унгол, где скрывается потайной ход в Темную страну.', 'Англия', '', 1, 9),
(10, 'Властелин Колец. Возвращение Короля', 1955, 'Джон Рональд Руэл Толкин', 'Повелитель сил Тьмы Саурон направляет свои бесчисленные рати под стены Минас-Тирита, крепости Последней Надежды. Он предвкушает близкую победу, но именно это и мешает ему заметить две крохотные фигурки — хоббитов, приближающихся к Роковой Горе, где им предстоит уничтожить Кольцо Всевластия. Улыбнется ли им счастье?..', 'Англия', '', 1, 9),
(11, 'Игра престолов', 1996, ' Джордж Мартин', 'Первая книга из цикла «Песнь льда и пламени» - лучшего эпического фентзи со времен Толкиена. Яркий фантазийный мир, напоминающий Англию времен войны Алой и Белой розы.\r\nНа земли Семи Королевств надвигается долгая изнурительная зима, и война за Железный трон началась. Лорд Эдвард Старк – страж Севера – призван королем и старинным другом Робертом на службу Десницей короля. Но Старые Боги не имеют силы на Юге, а лорд Старк слишком щепетилен для царедворца в вопросах чести. Предательство не за горами. А за горами подросла настоящая угроза трону – наследники свергнутого короля Дома Таргариенов – дети Дракона. На далеком Севере возвышается великая Стена, ограждающая королевство от древнего ужаса.', 'США', '', 2, 9),
(12, 'Фантастические звери и места их обитания', 2001, 'Джоан Кэтлин Роулинг', '«Фантастические звери и места их обитания» — это учебник по уходу за домашними животными, обязательный для всех первокурсников. Книга имеет в волшебном мире огромную ценность, да и у студентов Хогвартса не менее популярна. Все ее издания в «Завитках и кляксах» на Косой Аллее недолго задерживаются на полках, а сама она вышла, чтоб не ошибиться, уже где-то в 54-м переиздании. Из книги вы сможете узнать, кто такие клубкопухи, чем кормят драконов, сколько живут саламандры, что такое на самом деле Лох-Несское чудовище и почему мы не сталкиваемся каждый день с этими самыми тварями в жизни. Настоящая книга — копия той самой, которой пользовался Гарри, и посему она представлена читателю не в лучшем виде — вся исписана примечаниями Гарри, Рона и Гермионы (стыд и срам просто)!', 'Великобритания', '', 2, 9),
(13, 'Десять негритят', 1939, 'Агата Кристи', 'Роман “Десять негритят” - один из самых продаваемых бестселлеров на планете. В настоящий момент издано более 100 миллионов экземпляров по всему миру. Произведение многократно экранизировалось, адаптировалось под радиоспектакли и театральные постановки.\r\nВосемь человек получают письменные приглашения посетить уединенный остров под тем или иным предлогом. Каждому из них есть что скрывать и чего бояться. Неизвестный организатор этого странного мероприятия оставляет собравшимся запись, в которой обвиняет каждого из собравшихся в убийстве, оставшемся безнаказанным. Каждого ждет наказание в соответствие с детской считалочкой “Десять негритят” ', 'Англия', '', 2, 8),
(14, 'Корнуолльская тайна', 1979, 'Агата Кристи', 'Пуаро наносит визит миссис Пенгили, пожилая женщина, которая опасается за свою жизнь, думая, что ее хочет отравить собственный муж, дантист. Ей было очень плохо без еды, но доктор заверил женщину, что у нее лишь острый гастрит.\r\n\r\nОна с мужем проживают в Корнуолле, и у нее нет никаких доказательств дурных намерений мужа. У того нет даже финансовых мотивов вредить жене. Сама миссис Пенгили подозревает роман с молоденькой регистраторшей.\r\n\r\nВ их доме долгое время проживала племянница Фрида, но после ссоры, ей было отказано в доме, и та уехала.\r\n\r\nКогда, на следующий день, Пуаро и Гастингс прибыли в Корнуолл, их ждало шокирующее известие.', 'Англия', '', 4, 8),
(15, 'Последнее дело Холмса', 1891, 'Артур Конан Дойль', '«Последнее дело Холмса» - завершающий рассказ из авторского сборника «Воспоминания Шерлока Холмса». Первая журнальная публикация увидела свет в 1891 году, книжный вариант вышел двумя годами позже.\r\n\r\nКак позже заметил сам сэр Артур Конан Дойл, «Последнее дело Холмса» занимает четвертую строку в его собственном списке двенадцати лучших рассказов о Шерлоке Холмсе. Изначально предполагалось, что этим рассказом завершиться карьера великого сыщика. К тому времени Конан Дойл устал от своего героя и решил покончить с детективами, которые считал низким жанром. Писатель решил, что отныне будет работать над исторической прозой. Читатели, правда, рассудят иначе.\r\n\r\nА пока, выдающийся сыщик вступает в опасное для жизни единоборство с выдающимся преступником, профессором Мориарти.', 'Англия', '', 4, 8),
(16, 'Преступление и наказание', 1866, 'Федор Михайлович Достоевский', '«Преступление и наказание» - роман об одном преступлении. Двойное убийство, совершенное бедным студентом из-за денег. Трудно найти фабулу проще, но интеллектуальное и душевное потрясение, которое производит роман, неизгладимо. В чем здесь загадка? Кроме простого и очевидного ответа «в гениальности Достоевского», возможно, существует как минимум еще один: «проклятые» вопросы не имеют простых и положительных ответов. Нищета, собственные страдания и страдания близких всегда ставили и будут ставить человека перед выбором: имею ли я право преступить любой нравственный закон, чтобы потом стать спасителем униженных и утешителем слабых; должен ли я сперва возлюбить себя, а только потом, став сильным, возлюбить ближнего своего? Это вечные вопросы.', 'Российская империя', '', 2, 7),
(17, 'Портрет Дориана Грея', 1890, 'Оскар Уайльд', 'Оскар Уайльд написал восхитительную и ужасающую историю гламурного молодого человека, который продает свою душу за вечную молодость и красоту. Роман стал самым читаемым манифестом эстетических воззрений самого Оскара Уайльда и принес ему скандальную славу.\r\nПосле публикации романа в 1860 году, литературный ландшафт викторианской Англии претерпел существенные изменения. Демонстративный эстет, известный своим острым умом и насмешливостью, Уайльд услышал в свой адрес не только нелестные определения «вульгарно», «тлетворно», «безнравственно», но и подвергся судебному разбирательству по обвинению в грубой непристойности.', 'Англия', '', 2, 7),
(18, 'Белый Клык', 1906, ' Джек Лондон', 'Один из самых известных приключенческих романов Джека Лондона. Его главный герой - волк по кличке Белый Клык, прирученный людьми на Аляске во времена золотой лихорадки. Это полный драматизма рассказ от имени волка о людях. О разных людях. О жестоких и злых, о трусах и предателях, добрых и сострадательных, благородных и благодарных. Об всех, с кем пришлось столкнуться Белому Клыку на своем нелегком жизненном пути.\r\nЭта книга научит вас иными глазами взглянуть как на братьев наших меньших, так и на самих себя. Она несет себе заряд немыслимой очистительной силы, заставляет плакать, то от бессилия, то от радости. Она научит любить и ненавидеть, страдать и радоваться, оставаться настоящими людьми даже в нечеловеческих условиях.', 'США', '', 2, 7),
(19, 'Человек, который смеется ', 1869, 'Виктор Гюго', '1690 год. Группа бродяг срочно грузится на корабль, бросив, как ненужный хлам, полураздетого босого мальчишку. Он бредет по снегу, пока не натыкается на повешенного преступника, чья обувь может спасти его жизнь. Под виселицей он обнаруживает замерзшую женщину с еще живым младенцем на руках. Девочка слепа. Мала вероятность выжить даже самому, но мальчик не в силах бросить малышку. Вдвоем они натыкаются на фургон шарлатана Урсуса, и одна из самых романтичных и трагичных историй любви начинает набирать обороты. Мальчика ждут головокружительные изменения в судьбе, немыслимые для маленького бродяжки. Но принесут ли они ему долгожданный покой и счастье?', 'Франция', '', 2, 7),
(20, '1984', 1949, 'Джордж Оруэлл', '“Большой Брат смотрит на тебя!”. Мало найдется людей, кому не известна эта фраза, но далеко не все из них знают, что ее автор - Джордж Оруэлл и звучит она в романе-антиутопии “1984”.\r\n\r\nЛондон 1984 года - столица одной из трех сверхдержав, существующих на земле, Океании, в эпоху английского социализма. Мир постоянных войн. Океания живет под неусыпным руководством партии во главе с Большим Братом, квази-божественным лидером, воплощением культа личности. Тотальный контроль над разумом и чувствами осуществляется ради грядущего блага.\r\n\r\nГлавный герой романа, Уинстон Смит работает в министерстве правды, которое несет ответственность за осуществление партийной пропаганды и исторического ревизионизма. Являясь прилежным работником, Смит тайно ненавидит партию и мечтает о восстании против Большого Брата.', 'Англия', '', 2, 7),
(21, 'Хатынская повесть', 1971, 'Алесь Адамович', 'Оккупированная фашистской Германией Белоруссия подверглась тотальному террору. Каждый четвертый житель республики погиб в горниле войны. Фашисты стерли с лица земли 9 200 белорусских деревень. В более шестистах из них жители были или убиты, или сожжены живьем вместе со своими домами.\r\n \r\nФлориан Петрович Гайшун получил приглашение на открытие монумента в честь партизан, сложивших свои головы в белорусских лесах во время Великой Отечественной войны. В дороге он вспоминает свою партизанскую юность. Как подростком ушел в отряд, как встретил Глашу, как пережил трагическую гибель родной деревни и всей своей семьи.', 'СССР', '', 3, 6),
(22, 'Спартак', 1951, 'Говард Фаст', 'История гладиатора Спартака, его возлюбленной Варинии и честолюбивого римского полководца Красса. Непреодолимая тяга к свободе заставляет Спартака поднять легендарное восстание рабов, ставшее важнейшей вехой мировой истории.', 'США', '', 2, 6),
(23, 'Анна Каренина', 1878, 'Лев Николаевич Толстой', 'Роман  Льва Толстого  «Анна Каренина» входит в золотой фонд мировой литературы. Это монументальное произведение, в котором раскрыта тема семейной драмы. В нем основная линия о трагической любви замужней дамы Анны Карениной и офицера Вронского идет на фоне счастливого брака Константина Левина и Кити Щербацкой. Достоевский отзывался о романе Толстого  как об «огромной психологической разработке души человеческой». В этом масштабном произведении создана полная картина нравов и быта русского дворянского общества  второй половины XIX века, в которой, однако, философские размышления автора в лице  Левина сочетаются с психологическими зарисовки и сценами из жизни крестьян. \r\n\r\nВ этом  романе о частной жизни и отношениях современников Толстого, в котором автор зарисовал наблюдения за чувствами и мыслями реальных людей, поднимаются темы, важные для каждого. Имеем ли мы право разрушить семью, лишить ребенка материнской заботы ради буйства плоти?  Могут ли отношения, основанные на супружеской измене принести счастье или же они заранее обречены на провал? Неутолимая жажда быть понятым и в жизни, и в смерти, и эти вопросы без ответов стояли в прошлом, не уйти от них и сегодня, поэтому  этот  роман  будет современным в любую историческую эпоху. ', 'Российская империя', '', 2, 6),
(24, 'Марион Делорм', 1829, 'Виктор Гюго', '«Марио́н Дело́рм»  — романтическая трагедия в пяти действиях в стихах французского писателя, поэта и драматурга Виктора Гюго, в которой некоторые исторические образы и события переплетены со свободным вымыслом. Наряду с Людовиком XIII, кардиналом Ришельё, Марион Делорм и другими, Гюго рисует целую галерею вымышленных лиц. Драматический конфликт «Марион Делорм» основан на столкновении социально обездоленных, деклассированных, одиноких героев (Марион и Дидье) с мрачными силами абсолютисткой монархии, воплощёнными в образах Людовика XIII, Ришельё и исполнителей их велений.', 'Франция', '', 4, 6),
(25, 'Мост в Терабитию', 1977, 'Кэтрин Патерсон', 'Больше всего Джесс Аарон мечтает о том, чтобы стать самым стремительным бегуном в своем пятом классе. Он тренируется все лето и не может дождаться, когда увидит изумленные лица одноклассников в момент собственного триумфа. Но в первый же учебный день новичок, девочка, смело становится в группу мальчишек и обгоняет всех.\r\nЭто не очень многообещающее начало для дружбы, но вскоре Джесс и Лесли Брек становятся неразделимыми. Вместе, они создают Теревифию, волшебное королевство в лесу, где Лесли и Джесс правят как король и королева. Но однажды утром происходит ужасная и непредвидимая трагедия, и только вступив с ней в борьбу, Джесс понимает, какой силой и мужеством одарила его Лесли.', 'США', '', 3, 6),
(26, 'Двенадцать стульев', 1928, 'Ильф и Петров', 'Этот роман Ильфа и Петрова, эталон  юмора и сатиры,  для современного читателя до сих пор интересен, увлекателен и смешон. И это не смотря на то, что время и явления, в котором могли существовать герои данного романа ушло, но история поиска брильянтов мадам Петуховой, заставит похохотать любого.  Персонажи, далеко не положительные, завоевали народное призвание, а экранизации с харизматичными Гомиашвилли и Мироновым только усилили эту любовь.  Читателя ждет хороший слог и искрометный колкий юмор, а меткие  фразы типа «молодая была уже далеко не молода» и «может  тебе ещё ключи от квартиры, где деньги лежат?»  будут знакомы каждому, ведь они твердо и навсегда вошли в нашу речь. ', 'СССР', '', 2, 5),
(27, 'Собачье сердце', 1925, 'Михаил Афанасьевич Булгаков', '«Собачье сердце» —гениальное произведение Великого Булгакова, являющееся алмазом мировой литературы. В повести автор рассказывает читателю о Москве 1924года, ее жителях, правилах и нормах поведения, царящих в российском обществе того времени.  Выдающийся  хирург  профессор Филипп Филиппович Преображениский достиг невероятных результатов в  своей хирургической деятельности, а именно изобрел способ омоложения своих увядших пациентов, к слову весьма странный и комичный способ, но об этом читатель сам узнает из этого замечательного произведения. Профессор Преображенский в своей научной деятельности зашел так далеко, что решил провести невиданный эксперимент по пересадке человеческого гипофиза дворовой собаке Шарику. Результаты не только превзошли все ожидания, но детище эксперимента разочаровало своего создателя. Шарик принимает не только физический облик человека, но и, к сожалению,  доброе собачье сердце становится сердцем  человека «так себе». Шарик приобретает  психологию человека, правда человек получился с характером алкоголика, дебошира и повадками бездомного пса. Тем не менее, Гражданин Шариков так вписался в современное советское общество, что даже не смотря на собачьи мозги смог дослужиться до звания начальника.', 'СССР', '', 3, 5),
(28, 'Алиса в Стране Чудес. Алиса в Зазеркалье', 1864, 'Льюис Кэрролл', 'Алиса, скучающая на берегу реки, заглядывающая в читаемую сестрой книгу  и не понимающая, какой толк в книге, в которой совсем нет картинок, погружается в полуденный сон. И вдруг она видит Белого Кролика с карманными часами, бормочущего себе под нос и чрезвычайно спешащего. Любопытство одолевает Алису и в погоне за говорящим кроликом она сквозь кроличью нору попадает в чудный воображаемый мир. О странных приключениях Алисы в Стране Чудес  читатель узнает из этой потрясающей книги. ', 'Англия', '', 3, 5),
(29, 'Приключения Незнайки и его друзей', 1954, 'Николай Николаевич Носов', 'В сказочном Цветочном городке живут крошечные, с огурец, очаровательные коротышки. Самым обаятельным из них, тем, кто полюбился всем детям, стал Незнайка. Озорной, не очень прилежный, обожающий прихвастнуть Незнайка постоянно попадает в комические ситуации из-за излишней самоуверенности и нехватки знаний. Он неуемный фантазер и мечтатель, которому не хватает терпения и старания. Ему больше всех достается, он во все вмешивается, всем мешает, нарушает любые правила. Но у Незнайки доброе сердце и на мир он смотрит с неподдельным любопытством, стремясь исследовать в нем все, до последнего винтика.', 'СССР', '', 2, 5),
(30, 'Каштанка', 1887, 'Антон Павлович Чехов', 'Молодая собака потеряла своего хозяина, столяра Луку Александрыча. Она пытается найти его след, но безуспешно. Глубоким вечером, выбившись из сил, Каштанка засыпает у дверей подъезда, где её случайно находит некий таинственный незнакомец, впоследствии оказавшийся клоуном..', 'Российская Империя', '/images/books images/283_o.jpg', 3, 5);

