INSERT INTO `films` (`id`, `name`, `description`, `year`, `country`, `director`, `actors`, `baner`, `torrent`, `category_id`) VALUES
(1, 'Хоббит: Нежданное путешествие', 'Зрителей ждут удивительные путешествия Бильбо Бэггинса. Он отправляется в поход с одной единственной целью – вернуть гномам их Эребор, королевство,которое было отнято у них страшным драконом Смогом. В дороге он заводит знакомство с чародеем Гэндальфом Серым и они присоединяются к 30-ти гномам, воинство которых возглавляет Торин Дубощит. Их ждет долгая и опасная дорога по Дикому Краю, предательским землям, которые населяют орки и гоблины, пауки и народы, меняющие шкуры. Но, что бы попасть в пустоши Одинокой Горы, что находятся на Востоке, им необходимо пройти через туннели населенные гоблинами. Там-то и произойдет легендарная встреча Бильбо и Голлума. После этой встречи, в распоряжение Бильбо поступит простое золотое кольцо, с непростыми свойствами изменится не только его жизнь, но и всего Средиземья.', 2012, 'США, Новая Зеландия', 'Питер Джексон', 'Мартин Фриман, Иэн МакКеллен, Ричард Армитедж, Джеймс Несбитт, Кен Стотт, Грэм МакТавиш, Дин О’Горман, Эйдан Тернер, Стивен Хантер, Сильвестр Маккой', '/images/film images/1394942337_19dd52c.jpg', '/torrent/The.Hobbit.An.Unexpected.Journey.2012.BDRip720p_7916.torrent', 5),
(2, 'Хоббит: Пустошь Смауга', 'Бильбо Бэггинс, вместе с волшебником Гэндальфом и тринадцатью отважными гномами, продолжает свое путешествие к Одинокой горе. Им пришлось преодолеть нелегкий путь, полный опасностей, но впереди их ожидают куда более сложные испытания. Перейдя через Мглистые Горы, наши герои вынуждены были укрыться не только от страшных гигантских троллей, но и от орков. Теперь им пришлось продолжить свой путь через Лихолесье — заколдованный лес, который хранит в себе множество опасностей. Им предстоит встретиться с гигантскими пауками, недружелюбно настроенными эльфами, а также драконом Смаугом, пробудившимся после долгого сна.', 2013, 'США, Новая Зеландия', 'Питер Джексон', 'Мартин Фриман, Ричард Армитедж, Иэн МакКеллен, Эванджелин Лилли, Орландо Блум, Люк Эванс, Бенедикт Камбербэтч, Ли Пейс, Кен Стотт, Эйдан Тернер', '/images/film images/1394942488_fe41e9d.jpg', '/torrent/The_Hobbit.The_Desolation_of_Smaug.2013.720p.Bl_139024.torrent', 5),
(3, 'Хоббит: Битва пяти воинств', 'Торин и остальные гномы смогли добраться до логова Смауга, но разгневанный дракон<p> отправился к Эсгароту, чтобы расправиться со всеми его жителями. Метким выстрелом лучнику Барду удается остановить страшного монстра и спасти свой город. Хранитель сокровищ Эребора уничтожен, после чего остается один вопрос — кому достанутся несметные богатства: людям, гномам или эльфам? С этого момента Бильбо оказывается перед очень сложным выбором — чью сторону ему принять. А в это время к Эребору приближается посланная Некромантом армия орков, под командованием Азога, что приводит к началу Битвы Пяти Воинств.', 2014, 'США, Новая Зеландия', 'Питер Джексон', 'Мартин Фриман, Иэн МакКеллен, Ричард Армитедж, Джеймс Несбитт, Кен Стотт, Грэм МакТавиш, Уильям Кирчер, Стивен Хантер, Дин О’Горман, Эйдан Тернер', '/images/film images/1407588701_32cbf3e.jpg', '/torrent/Hobbit.Bitva.Pyati.Voinstv.2014.EC.DUAL.BDRip.7_242828.torrent', 10),
(4, 'Бэтмен: Начало', 'Одних людей смерть родителей угнетает и ввергает в отчаяние, а других – освобождает, расковывает и вдохновляет на движение вперед. Кто знает, как сложилась бы судьба «благополучного мальчика» Брюса Уэйна, единственного наследника родительских миллионов, если бы эта трагедия не произошла. Брюс, сравнительно спокойно перенесший утрату сразу отца и матери, отправляется в долгое путешествие.Когда он объездил все континенты, одолел воды всех океанов, закалился в лишениях и опасностях, лишь тогда он понял, что готов вернуться в родной Готэм, покинутый много лет назад. Но то, что нашел Уэйн по возвращении, не просто огорчило и удивило его, а просто перевернуло в нем душу.И вот тогда, поддерживаемый своим мудрым наставником, Брюс преображается. Надев плащ с широкими полами, напоминающими крылья летучей мыши,  и спрятав лицо под черной полумаской, Уэйн приходит на помощь тем, кто оказался в беде.Теперь этот образ Бэтмена становится не просто символом правоборца и покровителя слабых, но и мощным щитом против тех, кто творит беззаконие в его родном городе.', 2005, 'США, Великобритания', 'Кристофер Нолан', 'Кристиан Бэйл, Кэти Холмс, Майкл Кейн, Киллиан Мёрфи, Том Уилкинсон, Лиам Нисон, Кен Ватанабе, Гари Олдман, Морган Фриман, Рутгер Хауэр', '/images/film images/imgonline-com-ua-Resize-XbJrg12dDi.jpg', '/torrent/Betmen.Nachalo.2005.DUAL.BDRip.720p.x264.AC3.-H_7944.torrent', 2),
(9, 'Кошмар на улице Вязов', 'Молодой девушке по имени Тина Грей постоянно снится один и тот же ужасный сон, где жуткий человек с обгоревшим лицом, одетый в грязный полосатый свитер и перчаткой на правой руке с лезвиями вместо пальцев, пытается убить ее. Тина рассказывает о своем кошмаре подруге Нэнси и оказывается, что девушке снится тот же сон. Вскоре Тину находят убитой и подозрение падает на ее парня Рода и его вскоре арестовывают. Нэнси продолжают мучить ужасные видения и ее мать решает отвезти девушку в клинику по исследованию нарушений сна. Вскоре Нэнси узнает об истории этого человека из ночных кошмаров, которого зовут Фредди Крюгер. Он был маньяком, убивавшим детей, а родители его жертв решили отомстить Фредди и сожгли его заживо в котельной, где он обычно и работал...', 1984, 'США', 'Уэс Крэйвен', 'Хэзер Лэнгенкэмп, Рони Блейкли, Джон Сэксон, Аманда Уайсс, Роберт Инглунд, Джонни Депп, Чарльз Флайшер, Джозеф Випп, Хсу Гарсиа, Лин Шэй', '/images/film images/A_Nightmare_on_Elm_Street_poster_01.jpg', '', 7),
(10, 'Притяжение', 'Все мы уже привыкли, что в новостях нам то и дело рассказывают о военных конфликтах, террористических атаках, природных катаклизмах и прочих ненастьях. Но однажды жителей Москвы буквально ввел в ступор экстренный выпуск новостей. Диктор с экрана говорил вещи, в которые тяжело поверить: на столицу обрушился космический корабль. Количество жертв постоянно растет. Объявлено военное положение. В такой ситуации жители мегаполиса реагирует по разному: кто-то поддается панике, кто-то готовится к встрече с внеземным разумом. Но что хотят пришельцы от жителей Земли? Многие надеяться, что у них благие намерения. Но военные не могут рисковать. Министерство обороны страны готовится к самому худшему сценарию. Если все же им придется вступить в боевое столкновение с инопланетными гостями, то результат этой битвы тяжело предугадать. Ведь хотя у нас есть лучшее современное вооружение, не стоит недооценивать противника. Жителей столицы готовят к эвакуации. Только куда можно скрыться, если вторжение уже началось. Какая судьба уготована нам всевышним?', 2017, 'Россия', 'Фёдор Бондарчук', 'Ирина Старшенбаум\r\nАлександр Петров\r\nРиналь Мухаметов\r\nОлег Меньшиков\r\nСергей Гармаш\r\nЛюдмила Максакова', '/images/film images/Притяжение_(постер).jpg', '', 11),
(11, 'Вторжение', 'После падения инопланетного объекта, мир разделился на до и после. Юлию Лебедеву используют в качестве \"подопытного кролика\", так как она единственна была в контакте с пришельцем и она похоже с этим смирилась. В ней растет таинственная сила, которая наделяет девушку сверхъестественными способностями. Но ее феномен интересует не только землян, но и пришельцев, над планетой Земля нависает угроза нового вторжения. Чтобы выжить в этой схватке, людям придется найти в себе силы остаться людьми: милосердие, верность и любовь, сильнее инопланетных технологий...', 2019, 'Россия', 'Фёдор Бондарчук', 'Ирина Старшенбаум, Риналь Мухаметов, Александр Петров, Юрий Борисов, Сергей Гармаш, Олег Меньшиков, Даниил Кользенов, Сергей Троев, Игорь Костерин, Екатерина Степанова', '/images/film images/Вторжение_(фильм,_2019).jpg', '', 11);

